/**
* 将字段组件给出的数据结构转换为视图模型中的相关字段
* 
* @param fieldValue 字段组件数据
* @param viewModel 视图模型协议
* @param fieldCode 模型字段code
*/
function valueOut(fieldValue, viewModel, fieldCode) {
  const values = {};

  // 保证数据结构为数组
  let value = fieldValue;
  if (!Array.isArray(value)) {
    value = value ? [value] : [];
  }

  // 提取response进行数据提交
  value = value.map(item => {
    return item && item.response ? item.response : item;
  });

  values[fieldCode] = value;
  return values;
}

